code, name, company_id, remarks, create_by, create_date, update_by, update_date, del_flag, id,
) VALUES (
#{ code }, #{ name }, #{ companyId }, #{ remarks }, #{ createBy }, #{ createDate }, #{ updateBy }, #{ updateDate }, #{ delFlag }, #{ id },
)

mapper.update:
UPDATE tfam_nature SET
code = #{ code }, name = #{ name }, company_id = #{ companyId }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

UPDATE tfam_nature SET
update_by = #{updateBy},
update_date = #{updateDate},
del_flag = #{DEL_FLAG_DELETE}
WHERE id = #{id}
and update_date = #{oldUpdateDate}

