)

mapper.update:
UPDATE tgl_aging SET
begday = #{ begday }, aging = #{ aging }, tenant_id = #{ tenantId }, remark1 = #{ remark1 }, remarks = #{ remarks }, update_by = #{ updateBy }, update_date = #{ updateDate },
WHERE id = #{id} and update_date = #{oldUpdateDate}

